import React, { useState, useRef } from "react";
import useInfiniteScroll from 'react-infinite-scroll-hook';


const Search = ({scrollContainer}) => {
  const [searchString, setSearchString] = useState("");
  const [items, setItems] = useState([]);
  const [hasNextPage, setHasNextPage] = useState(false);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);


  const handleLoadMore = () => {

    setLoading(true);
    fetch(
      `http://localhost:4000/search/repositories?q=${searchString}&pages=${page}`,
    )
    .then(res => res.json())
    .then(res => {
      //  console.log(res)
       setLoading(false);
       let total = res.total_count
       let current = page * 30
       let hasNextPage = (current <= total)
       setHasNextPage(hasNextPage);
      //  setHasNextPage(false)
      //  console.log(res.items[0].name)
       setItems([...items, res.items]);
       console.log(items)
       setPage(page+1)
    })
    .catch(() => {
      console.log("stop it");
      setLoading(false);
    });
  }

  const infiniteRef = useInfiniteScroll({
    loading,
    hasNextPage,
    onLoadMore: handleLoadMore,
    scrollContainer,
  });

  const renderItems = () => {
    if (!items || items.length <= 0 ) return
    return items[0].map((item) => {
      console.log(item)
      return <li key={item.id}>{item.name}</li>
  })
}

  let onChange = e => {
    setItems([])
    setPage(0)
    setHasNextPage(true)
    setSearchString(e.target.value)
  }

  return (
    <div>
      <h1 className="header">Github Search</h1>
      <div className="gitSearchDiv">
        <input
          type="search"
          className="gitSearch"
          placeholder="search repo"
          onChange={e => onChange(e)}
        />
      </div>
       <ul ref={infiniteRef} style={{maxHeight:"10rem"}}>
        {renderItems()}
        {loading && <li>Loading...</li>}
      </ul>
    </div>
  );
};

export default Search;
