import React, { useState } from "react";
import useInfiniteScroll from 'react-infinite-scroll-hook';


const Search = () => {
  const [searchString, setSearchString] = useState("");
  const [items, setItems] = useState([]);
  const [hasNextPage, setHasNextPage] = useState(false);
  const [page, setPage] = useState(1);
  const [loading, setLoading] = useState(false);
  const [star, setStar] = useState()


  const handleLoadMore = () => {

    setLoading(true);
    fetch(
      `http://localhost:4000/search/repositories?q=${searchString}&page=${page}`,
    )
    .then(res => res.json())
    .then(res => {
      //  console.log(res)
       setLoading(false);
       let total = res.total_count
       let current = page * 30;
       let hasNextPage = (current <= total);
       setHasNextPage(hasNextPage);
      //  setHasNextPage(false)
       setItems([...items, res.items]);
      //  console.log(items)
       setStar(star)
       setPage(page+1)
    })
    .catch(() => {
      console.log("stop it");
      setLoading(false);
    });
  }

  const infiniteRef = useInfiniteScroll({
    loading,
    hasNextPage,
    onLoadMore: handleLoadMore,
    // scrollContainer,
  });

  const renderItems = () => {
    if (!items && searchString === "" || items.length <= 0 && searchString === "" ) return
    return items[0].map((item) => {
      // console.log(item.name)
      return <li key={item.id}>Name: {item.name}, Stars: {item.stargazers_count}</li>
  })
}

  let onChange = e => {
    setItems([])
    setPage(0)
    setHasNextPage(true)
    setSearchString(e.target.value)
  }

  let onClick = () => {
    alert("clicked")
  }

  return (
    <div>
      <h1 className="header">Github Search</h1>
      <div className="gitSearchDiv">
        <input
          type="search"
          className="gitSearch"
          placeholder="search repo"
          onChange={e => onChange(e)}
        />
      </div>
       <ul ref={infiniteRef} onClick={onClick}>
        {renderItems()}
        {/* {loading && <li>Loading...</li>} */}
      </ul>
    </div>
  );
};

export default Search;
