import React, { useState, useEffect } from "react";


const Search = () => {
  const [query, setQuery] = useState("");
  const [stars, setStars ] = useState();
  const [items, setItems] = useState([]);
  const [languages, setLanguages] = useState({
    JavaScript: false,
    Python: false, 
    Assembly: false,
    Ruby: false,
    Java: false,
    Swift: false
  })

  useEffect(() => {
    // console.log("hello")
    if (!query) return;

    const fetchData = async () => {
      const response = await fetch(
        `http://localhost:5000/search/repositories?searchTerms=${query}&numStars=${stars}`
      );
      const items = await response.json();
      setItems(items.items);
      setStars(items.stars)
      console.log(items.langauge)
    };
    fetchData()
  }, [query, stars])




  // const handleSubmit = (e) => {

  //   console.log(e.currentTarget.value)

  //   e.preventDefault();

  //   console.log(e.target.value)

  //   const query = e.target.value;

  //   if(query) {
  //     setItems([])
  //     setSearchString(query);
  //     e.target.value = ""
  //   }

  // }

  // const onChange = e => {
  //   console.log(e.target.value)
  //       setItems([])
  //       setSearchString(e.target.value)
  //     }

  const handleChange = (e) => {
      setItems([]);
      setStars(stars)
      setQuery(e.target.value);
    }

  const handleSubmit = (e) => {
      e.preventDefault();
      // fetch search results
    }

  const checkBoxTypeClick = e => {
    setStars(stars)
  }

  let checkBoxLanguageClick = e => {
    let tempLanguage = { ...languages };
    tempLanguage[e.target.language] != languages[e.target.langauge];
    setLanguages(tempLanguage);
  }



  const renderItems = () => {
    // console.log(items)
    if (!items || items.length <= 0 ) return
    return items.map((item) => {
      return <li key={item.id}>Name: {item.name}, Stars: {item.stargazers_count}, Language: {item.language}</li>
    })
  }

  return (
      <div>
        <h1 className="header">Github Search</h1>
        <form className="Form" onSubmit={handleSubmit}>
          <input
              type="text"
              autoFocus
              className="gitSearch"
              placeholder="search repo"
              onChange={handleChange}
              value={query}
            />
          <button>Search</button>
        </form>
        <div className="checkBoxes">
          <label>Sort by Stars</label>
          <input
            type="checkbox"
            id="stars"
            name="stars"
            onClick={e => checkBoxTypeClick(e)}
          >
          </input>
          <label>Sort by Score</label>
          <input
            type="checkbox"
            id="score"
            name="score"
            onClick={e => checkBoxTypeClick(e)}
          >
          </input>
          <label>Languages</label>
          <input
            type="checkbox"
            id="Python"
            name="Python"
            onClick={e => checkBoxLanguageClick(e)}
          >
          </input>

        </div>
        <ul>
          {renderItems()}
        </ul>
      </div>
    );
  };


export default Search;
